import React from "react";
import ImgRedonda from "../../Global/ImgRedonda";
import { Fragment } from "react";

const Carro = (props) => {

    const propriedades = ['Revisão anual', 'Manual de fábrica', 'Pintura original']
    const listaPropriedades = propriedades.map((n, index) => 
        <li key={index}>{n}</li>
    )

    if(props.img) {
        
            return (
        
                <Fragment>
        
                    <h3>{props.titulo}</h3>
                    
                    <p>{props.descricao}</p>
        
                    <ImgRedonda img={props.img}></ImgRedonda>

                    <ul>
                        {listaPropriedades}
                    </ul>
                </Fragment>
        
            )

    } else {

        return (
        
            <Fragment>
    
                <h3>{props.titulo}</h3>
                
                <p>{props.descricao}</p>
    
                <ImgRedonda img="data:image/png;base64,"></ImgRedonda>
            
            </Fragment>
    
        )
    }
}

export default Carro;